  'slow': 150,    // Lento: ~150 caracteres/min
  'normal': 250,  // Normal: ~250 caracteres/min
  'fast': 400     // R√°pido: ~400 caracteres/min
};
const caracteresPorMinuto = speedMultipliers[typingSpeed] || 250;
