const variacao = 0.2;
const aleatorio = 1 + (Math.random() * variacao * 2 - variacao);
const tempoFinalSegundos = Math.ceil(tempoEmSegundos * aleatorio);
// Tempo m√≠nimo baseado na velocidade configurada
const temposMinimosPorVelocidade = {
  'slow': 4,
  'normal': 2,
  'fast': 1
};
const tempoMinimo = temposMinimosPorVelocidade[typingSpeed] || 2;
const tempoMinimoSegundos = Math.max(tempoFinalSegundos, tempoMinimo);
// Converte para milissegundos
const tempoEmMilissegundos = tempoMinimoSegundos * 1000;
// Retorna os dados incluindo responseMode para usar no IF do N8N
return {
  json: {
    mensagem: mensagem,
    totalCaracteres: totalCaracteres,
    typingSpeed: typingSpeed,
