    caracteresPorMinuto: caracteresPorMinuto,
    tempoEsperaSegundos: tempoMinimoSegundos,
    tempoEsperaMilissegundos: tempoEmMilissegundos,
    tempoEsperaFormatado: `${tempoMinimoSegundos}s (${typingSpeed})`
  }
};

